package week1.day2;

public class OddorEven {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
			int ckNo = 241;
			int flgMod = ckNo%2;
			if(flgMod == 0)
				System.out.println("Given Number is an Even number");
			else
				System.out.println("Given Number is an Odd number");
			
	}

}
